sources.main.scss:
@font-face {
    font-family: 'ChosunSg';
    src: url('https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_20-04@1.0/ChosunSg.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

$title: 'chosunKg';
$content: 'chosunSg';
$font-awesome: "Font Awesome 6 Free";
$text-color-W: white;
$text-color-light-grey: #ccc;
$text-color-B: black;
$text-color-weak: #ffffff33;
$num-of-cording: 4;
$num-of-design: 10;

@mixin grid($max) {
    width: 100%;
    max-width: $max;
    margin: auto;
}

@mixin center($justify) {
    display: flex;
    justify-content: $justify;
    align-items: center;
}

@mixin text-indent() {
    display: block;
    text-indent: -9999px;
    overflow: hidden;
}

@mixin width($width) {
    width: 100%;
    max-width: $width;
}

@mixin height($height) {
    height: 100%;
    max-height: $height;
}

@mixin font($family, $size, $weight) {
    font: {
        family: $family;
        size: $size;
        weight: $weight
    };
}

@mixin h2 {

    text: {
        transform: uppercase;
        align: center;
    };

    color: $text-color-W;
    margin: 2rem;
    letter-spacing: .4px;
}

@mixin h3 {

    text: {
        transform: uppercase;
        align: center;
    }

    color: $text-color-W;
}

@mixin fadein-opacity($duration) {
    opacity: 0;
    transition: $duration;

    &.on{
        opacity: 1;
    }
}

@mixin p ($size) {
    font: {
        family: $content;
        size: $size;
    }

    color: $text-color-W;
}

@keyframes updown {
    0% {
        transform: translateY(-5px);
    }

    50% {
        transform: translateY(5px);
    }

    100% {
        transform: translateY(-5px);
    }
}

@keyframes fade-in-x {
    0% {
        opacity: 0;
        transform: translateX(-200%);
    }


    100% {
        opacity: 1;
        transform: translateX(0%);
    }
}

@keyframes fade-in-y {
    0% {
        opacity: 0;
        transform: translatey(100%);
    }


    100% {
        opacity: 1;
        transform: translateX(0%);
    }
}

@keyframes opacity {
    0% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}


@keyframes scroll-down-information {
    0% {
        opacity: 1;
    }

    50% {
        opacity: .5;
    }

    100% {
        opacity: 1;
    }
}

@keyframes twinkle {
    0% {
        background-position-x: 0;
    }

    100% {
        background-position-x: -1000px;
    }
}

@keyframes card-flip {
    0%{
        transform: perspective(100vw) translateX(100vw) rotateY(360deg);
    }
    100%{
        transform: perspective(100vw) translateX(0) rotateY(0);
    }
}

* {
    margin: 0;
    padding: 0;
    list-style: none;
}

.screen_out {
    display: none;
}

body {
    background-color: $text-color-B;
    opacity: 1;
    background-image: url(img/stars.png);
    overflow-x: hidden;

    .twinkle {
        background-image: url(img/twinkle.png);
        animation: twinkle 50s linear infinite;
    }

    &.no-scroll{
        height: 100%;
        overflow: hidden;
    }
}

header {
    padding: 0 40px;

    h1 {
        @include text-indent;
        width: 100px;
        height: 80px;
        background: url(./img/8th_logo_white.png) center / 60px 50px no-repeat;
    }

    nav {
        @include grid(1600px);
        @include center(space-between);
        margin: auto;

        ul {
            display: flex;
            width: fit-content;
            justify-content: right;

            li {
                margin: 1rem 2rem;
                position: relative;
                color: aliceblue;
                text-decoration: none;
                text-transform: uppercase;
                font-size: 20px;
                font-family: $content;
                width: fit-content;
                cursor: pointer;

                &::after {
                    content: "";
                    position: absolute;
                    bottom: -5px;
                    left: 0;
                    width: 0;
                    height: 1.5px;
                    background-color: aliceblue;
                    border-radius: 50%;
                    transition: .2s ease-in;
                }

                &.on::after {
                    width: 100%;
                }

                &:hover::after {
                    width: 100%;
                }
            }
        }
    }

    &>h1 {
        position: fixed;
        top: 42%;
        transform: translateY(-50%);
        right: 3.5%;
        width: 50px;
        height: 40px;
        background-size: 90%;
        opacity: 0;
    }

    &>ul {
        width: fit-content;
        position: fixed;
        top: 50%;
        right: 1%;
        transform: translateY(-50%);

        li {
            color: $text-color-W;
            opacity: .3;
            text-transform: uppercase;
            margin: 1rem;
            cursor: pointer;
            @include font($content, 14px, lighter);

            &:hover {
                opacity: 1;
            }

            &.on {
                opacity: 1;
            }
        }
    }

    .mobile {
        position: absolute;
        right: 30px;
        top: 50%;
        transform: translateY(-50%);
        width: 30px;
        height: 25px;
        background-color: transparent;
        border: none;
        outline: none;
        overflow: hidden;
        display: none;

        span {
            text-indent: -9999px;
            overflow: hidden;
            width: 30px;
            height: 25px;
            position: relative;
            display: block;
            transition: .3s all;

            &::before {
                content: "";
                position: absolute;
                width: 100%;
                height: 2px;
                background-color: #fff;
                top: 0;
                left: 0;
                transition: .3s all;
            }

            &::after {
                content: "";
                position: absolute;
                width: 100%;
                height: 2px;
                background-color: #fff;
                bottom: 0;
                left: 0;
                transition: .3s all;
            }
        }

    }
}

main {
    .slogan {
        height: 90vh;
        overflow: hidden;
        @include grid(1600px);
        display: flex;
        justify-content: center;

        .ufo {
            width: 200px;
            height: 100px;
            background: url(img/ufo.png) center/135px 75.8px no-repeat;
            object-fit: cover;
            transition: ease all 7S;
            position: absolute;
        }

        .orbit {
            height: 600px;
            width: 600px;
            margin: auto;
            animation: updown 3s linear infinite;
        }

        .sloganT {
            color: $text-color-W;
            height: fit-content;
            position: absolute;
            bottom: 20%;

            text: {
                transform: uppercase;
                align: center;
            }

            font: {
                size: 1.5rem;
                family: $content;
            }

            cursor: default;
            line-height: 120%;
        }

        .scroll {
            position: relative;
            color: grey;
            margin: 2rem;
            position: absolute;
            bottom: 12%;
            animation: scroll-down-information 3s ease-in infinite;

            text: {
                align: center;
                transform: uppercase;
            }

            &::after {
                content: "";
                position: absolute;
                bottom: 0px;
                left: 50%;
                transform: translate(-50%, 110%) rotate(-45deg);
                width: 15px;
                height: 15px;

                border: {
                    bottom: 1px solid grey;
                    left: 1px solid grey;
                }
            }
        }
    }

    .about {
        padding-top: 80px;

        h2 {
            @include h2;
            @include font($title, 2em, bold);
        }

        .profile {
            display: flex;
            @include center(center);

            img {
                @include width(600px);
                opacity: 0;

                &.on {
                    opacity: 1;
                    transition: .6s ease-out;
                }
            }

            div {
                position: relative;
                transform: translateY(50%);
                line-height: 150%;
                color: $text-color-W;

                h3 {
                    @include h3;
                    @include font($content, 24px, bold);
                    transform: translateY(-200%);
                    opacity: 0;
                    transition: .6s ease-out;
                    text-align: left;

                    &.on {
                        opacity: 1;
                        transform: translateY(0);
                    }
                }

                ul {
                    margin-top: 5%;
                    line-height: 130%;
                    word-break: keep-all;
                    color: $text-color-W;
                    transform: translateY(-100%);
                    opacity: 0;
                    transition: .8s ease-out;
                    transition-delay: .4s;

                    &.on {
                        opacity: 1;
                        transform: translateY(0);
                    }

                    li {
                        p {
                            @include p(16px);
                        }
                    }
                }

                button {
                    padding: 5px 10px;
                    background: transparent;
                    border: 1px solid $text-color-W;
                    border-radius: 2px;
                    text-transform: uppercase;
                    color: $text-color-W;
                    position: absolute;
                    left: 0;
                    bottom: -30%;
                    opacity: 0;
                    transition: .5s ease-out;

                    &:hover {
                        opacity: 1;
                        cursor: pointer;
                    }

                    &.on {
                        opacity: .4;
                        transition-delay: 1s
                    }
                }
            }

            &+details {
                @include grid(1200px);
                overflow: hidden;
                transition: .5s ease-in-out;
                width: 0;
                height: 0;

                ul {
                    display: grid;
                    margin-top: 2rem;
                    grid-template-columns: repeat(8, 1fr);

                    li {
                        color: $text-color-W;
                        height: 160px;
                        background: url(img/html5.svg) center / 50% no-repeat;
                        position: relative;

                        span {
                            text-transform: uppercase;
                            position: absolute;
                            left: 50%;
                            bottom: 5%;
                            transform: translate(-50%, -50%);
                            font-size: 12px;
                            width: 100%;
                            text-align: center;
                        }

                        &.css {
                            background-image: url(img/css.svg);
                        }

                        &.js {
                            background-image: url(img/square-js.svg);
                        }

                        &.jquery {
                            background-image: url(img/jquery.svg);
                        }

                        &.github {
                            background-image: url(img/github.png);
                        }

                        &.scss {
                            background-image: url(img/sass.png);
                        }

                        &.figma {
                            background-image: url(img/figma.png);
                        }

                        &.photoshop {
                            background-image: url(img/photoshop.png);
                        }

                        &.illur {
                            background-image: url(img/illustrator.png);
                        }

                        &.premier {
                            background-image: url(img/premiere-pro.png);
                        }

                        &.affect {
                            background-image: url(img/after-effects.png);
                        }
                    }
                }

                &.on {
                    width: 100%;
                    height: 100%;
                }
            }
        }

        .study {
            margin-top: 120px;

            h3 {
                @include h2;
                @include font($title, 24px, bold);
                margin: 2rem 0;
                opacity: 0;
                transition: .3s;

                &.on{
                    opacity: 1;
                }
            }

            &>ul {
                display: flex;
                gap: 10px;
                justify-content: center;

                .container {
                    position: relative;
                    border-radius: 12px;
                    @include width(300px);
                    height: 360px;
                    padding: 16px;
                    box-sizing: border-box;

                    &:hover .card.in{
                        transform: rotateY(180deg);
                    }

                    .card {
                        position: absolute;
                        width: 100%;
                        height: 100%;
                        transform: perspective(100vw) translateX(100vw) rotateY(360deg);
                        transform-style: preserve-3d;
                        transition: all .7s ease;

                        &.in {
                            animation: card-flip 1.7s ease-out normal;
                            transform: perspective(100vw) translateX(0) rotateY(0);
                        }

                        .front {
                            position: absolute;
                            width: 100%;
                            height: 100%;
                            border: 2px solid $text-color-W;
                            box-sizing: border-box;
                            border-radius: 12px;
                            backface-visibility: hidden;
                            color: $text-color-W;
                            background: url(img/html5.svg) center / 50% no-repeat;
                            animation: updown 5s ease-out infinite;

                            &.css {
                                background-image: url(img/css.svg);
                                animation: updown 5.1s linear infinite;
                            }

                            &.vanila {
                                background-image: url(img/square-js.svg);
                                animation: updown 5.2s linear infinite;
                            }

                            &.jquery {
                                background-image: url(img/jquery.svg);
                                animation: updown 5.3s linear infinite;
                            }

                            span {
                                @include font($content, 20px, light);
                                position: absolute;
                                left: 50%;
                                bottom: 0;
                                transform: translate(-50%, -100%);
                            }
                        }

                        .back {
                            position: absolute;
                            width: 100%;
                            height: 100%;
                            backface-visibility: hidden;
                            transform: rotateY(180deg);
                            border-radius: 12px;
                            border: 2px solid $text-color-W;
                            padding: 0 16px;
                            box-sizing: border-box;

                            h4 {
                                @include h3;
                                @include font($title, 16px, bold);
                                margin: 20px 0;
                                position: relative;
                                word-break: keep-all;

                                &::after {
                                    content: "";
                                    position: absolute;
                                    left: 0;
                                    bottom: -8px;
                                    width: 100%;
                                    height: 1px;
                                    background-color: rgba($color: #fff, $alpha: .3);
                                }

                                &+p {
                                    @include font($content, 12px, light);
                                    color: $text-color-W;
                                    margin: 0 0 28px;
                                    text-align: center;
                                    line-height: 130%;
                                    word-break: keep-all
                                }
                            }

                            ul {
                                display: grid;
                                grid-template-columns: repeat(2, 1fr);
                                column-gap: 5px;

                                li {
                                    @include p(14px);
                                    transform: translateX(4%);
                                    margin: 0 0 16px;
                                    text-transform: uppercase;
                                    word-break: keep-all;
                                    line-height: 120%;
                                }
                            }


                            a {
                                border: 1px solid $text-color-W;
                                text-decoration: none;
                                color: $text-color-W;
                                padding: 5px 10px;
                                @include font($content, 14px, light);
                                position: absolute;
                                right: 20px;
                                bottom: 20px;
                                opacity: .3;

                                &:hover {
                                    opacity: 1;
                                }
                            }
                        }
                    }

                    span {
                        color: $text-color-W;
                        text-transform: uppercase;
                    }
                }
            }
        }

        .merit {
            position: relative;
            height: 100%;
            margin-top: 200px;

            svg {
                opacity: .8;
                position: fixed;
                top: 50%;
                left: 50%;
                transform: translateX(-50%);
                z-index: -1;
                transition: ease-in-out .3s;

                .path {
                    opacity: 0;
                    transition: ease-in-out .5s;

                    &.on {
                        opacity: .5;
                    }

                    @for $i from 1 through 13 {
                        $delay: .1s;

                        &.on:nth-of-type(#{$i}) {
                            transition-delay: $delay*$i;
                        }
                    }
                }

                &.on {
                    z-index: 1;
                }
            }

            .wrap {
                top: 0;
                overflow: hidden;
                position: relative;
                height: 200vh;

                ul {
                    display: grid;
                    grid-template-columns: repeat(2, 1fr);
                    grid-template-rows: repeat(6, 1fr);
                    @include grid(1240px);
                    row-gap: 1.5rem;
                    column-gap: calc(1240px / 3);
                    height: 100%;
                    box-sizing: border-box;

                    grid-template-areas:
                        "left1." ".right1"
                        "left2." ".right2"
                        "left3." ".right3";

                    .good {
                        border-radius: 12px;
                        @include width(450px);
                        @include height(150px);
                        padding: 1.5rem .2rem 0rem;
                        border: 2px solid $text-color-W;
                        background-color: $text-color-B;
                        position: relative;
                        opacity: 0;
                        transition: ease-in-out .7s;
                        z-index: 1;


                        @for $i from 1 through 3 {
                            &.left#{$i} {
                                grid-area: left#{$i};
                            }

                            &.right#{$i} {
                                grid-area: right#{$i};

                                @if $i ==1 {
                                    right: 0;
                                }
                            }
                        }

                        &:hover {
                            background-color: #fff;
                        }

                        h4 {
                            @include h3;
                            @include font($title, 1.5rem, bold);
                            margin: 0 0 16px;
                            color: $text-color-W;
                        }

                        p {
                            word-break: keep-all;
                            @include font($content, 1.1rem, light);
                            line-height: 130%;
                            text-align: center;
                            color: $text-color-W;
                        }

                        &:hover h4,
                        &:hover p {
                            color: $text-color-B;
                        }

                        &.on {
                            animation: fade-in-y .5s ease-out normal;
                            opacity: 1;

                            @for $i from 1 through 6 {
                                $delay: .5s;

                                &.on:nth-of-type(#{$i}) {
                                    animation-duration: $delay*$i;
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    .portfolio {

        h2 {
            @include h2;
            @include font($title, 32px, bold);
        }

        .wrap{
            @include fadein-opacity(.8s);

            &>ul {
                @include center(space-around);
                @include width(250px);
                margin: auto;
    
                button {
                    color: $text-color-weak;
                    border: 2px solid $text-color-weak;
                    border-radius: 35px;
                    font-family: $title;
                    font-size: 16px;
                    padding: 7px 20px;
                    cursor: pointer;
                    background-color: transparent;
                    text-transform: uppercase;
    
                    &:hover {
                        color: $text-color-W;
                        border: 2px solid $text-color-W;
                    }
    
                    &.on {
                        color: #ffffff;
                        border: 2px solid $text-color-W;
                        background-color: $text-color-weak;
                    }
                }
            }
        }

        .cording {
            ul {
                @include grid(1200px);
                margin-top: 80px;

                li {
                    border-bottom: 2px solid $text-color-W;
                    position: relative;
                    opacity: 0;

                    &.on {
                        animation: fade-in-x .5s ease-out normal;
                        opacity: 1;

                        @for $i from 1 through $num-of-cording {
                            $delay: .2s;

                            &.on:nth-of-type(#{$i}) {
                                animation-duration: 1 + ($delay*$i);
                            }
                        }
                    }

                    a {
                        text-decoration: none;
                        color: $text-color-W;
                        text-transform: uppercase;
                        display: flex;
                        align-items: center;
                        width: 100%;
                        padding: 2rem;
                        @include font($title, 1.5rem, bold);

                        .cording-img {
                            position: absolute;
                            top: -100%;
                            right: 0;
                            width: 200px;
                            height: 200px;
                            border-radius: 50%;
                            overflow: hidden;
                            background-position: center;
                            background-size: cover;
                            background-repeat: no-repeat;
                            transition: all .3s ease-out;
                            opacity: 0;
                        }
                    }

                    &:hover .cording-img{
                        opacity: 1;
                    }

                    &:nth-of-type(1) .cording-img {
                        background-image: url(img/uiux_pt/셀렉토커피홈페이지.png);
                    }

                    &:nth-of-type(2) .cording-img {
                        background-image: url(img/uiux_pt/현대그린푸드_홈페이지.png);
                    }

                    &:nth-of-type(3) .cording-img {
                        background-image: url(img/uiux_pt/삼성디스플레이뉴스룸.png);
                    }

                    &:nth-of-type(4) .cording-img {
                        background-image: url(img/uiux_pt/8th포트폴리오페이지.png);
                    }
                }
            }
        }

        .design {
            @include grid(1200px);
            margin-top: 80px;

            &>ul {
                display: grid;
                grid-template-columns: repeat(4, 1fr);
                row-gap: 1rem;

                &>li {
                    @include width(280px);
                    border-radius: 8px;
                    overflow: hidden;
                    position: relative;
                    opacity: 0;
                    cursor: pointer;

                    &.on {
                        animation: opacity .3s ease-in normal;
                        opacity: 1;

                        @for $i from 1 through $num-of-design {
                            $delay: .1s;

                            &.on:nth-of-type(#{$i}) {
                                animation-duration: $delay*$i;
                            }
                        }
                    }

                    &>p {
                        @include font($title, 16px, bold);
                        text-transform: uppercase;
                        text-align: center;
                        color: $text-color-W;
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        z-index: 1;
                        display: none;
                    }

                    &>img {
                        width: 100%;
                    }

                    &:hover>p {
                        display: block;
                    }

                    &:hover img {
                        filter: brightness(.3);
                    }

                    details {
                        position: fixed;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        display: none;
                        z-index: 2;
                        background-color: $text-color-B;
                        border: 1px solid $text-color-light-grey;
                        padding: 2rem;
                        border-radius: 12px;

                        &.on {
                            display: block
                        }

                        img,
                        video {
                            @include width(400px);
                            margin: 1.5rem 0;
                        }

                        p {
                            color: $text-color-W;
                            text-transform: uppercase;
                            @include font($title, 20px, bold);
                            margin: 0 0 1rem 0;
                        }

                        ul {
                            display: flex;
                            justify-content: space-between;

                            li {
                                color: $text-color-W;
                                text-transform: uppercase;
                                @include font($content, 14px, lighter)
                            }
                        }
                    }
                }
            }
        }
    }

    .contact {
        margin: 170px auto;

        h2 {
            color: $text-color-W;
            text-transform: uppercase;
            @include font($title, 2rem, bold);
            text-align: center;
            margin: 80px 0;
        }

        .wrap {
            @include grid(1200px);
            position: relative;
            @include center(space-around);
            @include height(100%);

            .inform {
                height: 100%;
                transform: translateY(-30%);

                p {
                    color: $text-color-W;
                    @include font($title, 24px, bold);
                    line-height: 120%;
                    display: flex;
                    flex-direction: column;
                    align-items: flex-start;
                    gap: 1em;
                    position: relative;

                    span {
                        @include font($content, 16px, lighter);
                        color: $text-color-light-grey;
                    }
                }

                ul {
                    left: 0;
                    top: 50%;
                    height: 50px;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    transform: translateY(50%);

                    li {
                        color: $text-color-light-grey;
                        font-family: $content;
                        font-size: 16px;
                        line-height: 30px;

                        span {
                            width: 20px;
                            height: 20px;
                            text-indent: -9999px;
                            overflow: hidden;
                            background: url(img/katalk.svg) center / 20px no-repeat;
                            display: inline-block;
                        }

                        &:nth-child(2) span {
                            background-image: url(img/email.svg);
                        }
                    }
                }
            }


            form {
                width: 100%;
                max-width: 500px;

                fieldset {
                    border: none;
                    width: 100%;

                    input {
                        height: 40px;
                        font-size: 18px;
                        padding: 0 20px;
                        border: 2px solid $text-color-weak;
                        border-radius: 25px;
                        background: transparent;
                        outline: none;
                        color: #fff;
                        font-family: $content;
                        box-sizing: border-box;
                        display: block;
                        width: 100%;
                        margin-bottom: 15px;

                        &:focus {
                            border: 2px solid #fff;
                        }

                        &+label {
                            color: $text-color-weak;
                            font-family: $content;
                            font-size: 18px;
                            height: fit-content;
                            width: fit-content;
                            position: absolute;
                            margin: -45px 0 0 20px;
                            padding: 0 10px;
                            transition: all .3s;
                            background-color: $text-color-B;
                            border-radius: 20px;
                        }

                        &:focus+label,
                        &.on+label {
                            margin: -60px 0 0 20px;
                            font-size: 14px;
                            color: #fff;
                            z-index: 1;
                        }
                    }

                    textarea {
                        border: 2px solid $text-color-weak;
                        border-radius: 25px;
                        background: transparent;
                        outline: none;
                        color: #fff;
                        font-family: $content;
                        box-sizing: border-box;
                        display: block;
                        width: 100%;
                        margin-bottom: 15px;
                        height: 100%;
                        padding: 20px;
                        font-size: 18px;

                        &:focus {
                            border: 2px solid #fff;
                        }

                        &+label {
                            position: absolute;
                            margin: -221px 0 0 20px;
                            padding: 0 10px;
                            transition: all .3s;
                            background-color: $text-color-B;
                            border-radius: 20px;
                            color: $text-color-weak;
                            font-family: $content;
                            font-size: 18px;
                            height: fit-content;
                            width: fit-content;
                        }

                        &:focus+label,
                        &.on+label {
                            font-size: 14px;
                            color: #fff;
                            margin: -240px 0 0 20px;
                        }
                    }

                    #submit {
                        width: 90px;
                        height: 35px;
                        border: 2px solid $text-color-weak;
                        background-color: transparent;
                        color: $text-color-weak;
                        outline: none;
                        border-radius: 25px;
                        font-size: 18px;
                        font-family: var(--content);
                        position: absolute;
                        right: 0px;
                        transform: translateX(-100%);

                        &:hover {
                            color: #fff;
                            border-color: #fff;
                        }
                    }

                }
            }
        }
    }

    .back-drop {
        position: fixed;
        top: 0;
        left: 0;
        width: 100vw;
        height: 100vh;
        background-color: $text-color-B;
        opacity: .8;
        z-index: 0;
        display: none;

        &.on {
            display: block;
        }
    }

    footer {
        padding: 30px;
        border-top: 1px solid $text-color-weak;
        background: $text-color-B;

        h2 {
            text-indent: -9999px;
            overflow: hidden;
            width: 60px;
            height: 50px;
            background: url(img/8th_logo_white.png) center/60px no-repeat;
            opacity: .4;
            margin: auto;
            display: block;
        }

        address {
            color: $text-color-light-grey;
            font-style: normal;
            text-align: center;
            font-size: 12px;
            font-family: $content;
        }

        p {
            color: $text-color-light-grey;
            opacity: .3;
            text-align: center;
            font-size: 12px;
            font-family: $content;
        }
    }
}
