@font-face {
    font-family: 'ChosunSg';
    src: url('https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_20-04@1.0/ChosunSg.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

$title: 'chosunKg';
$content: 'chosunSg';
$font-awesome: "Font Awesome 6 Free";
$text-color-W: white;

@mixin grid($max) {
    width: 100%;
    max-width: $max;
    margin: auto;
}

@mixin center($justify) {
    display: flex;
    justify-content: $justify;
    align-items: center;
}

@mixin text-indent() {
    display: block;
    text-indent: -9999px;
    overflow: hidden;
}

@mixin width($width) {
    width: 100%;
    max-width: $width;
}

@mixin height($height) {
    height: 100%;
    max-height: $height;
}

@mixin font($family, $size, $weight) {
    font: {
        family: $family;
        size: $size;
        weight: $weight
    }

    ;
}

@mixin h2 {

    text: {
        transform: uppercase;
        align: center;
    }

    ;

    color: $text-color-W;
    margin: 2rem;
    letter-spacing: .4px;
}

@mixin h3 {

    text: {
        transform: uppercase;
        align: center;
    }

    ;

    color: $text-color-W;

}

@mixin p ($size) {
    font: {
        family: $content;
        size: $size;
    }

    color: $text-color-W;
}

@keyframes updown {
    0% {
        transform: translateY(0px);
    }

    25% {
        transform: translateY(-5px);
    }

    50% {
        transform: translateY(0px);
    }

    75% {
        transform: translateY(5px);
    }

    100% {
        transform: translateY(0px);
    }
}


@keyframes opacity {
    0% {
        opacity: 1;
    }

    50% {
        opacity: .5;
    }

    100% {
        opacity: 1;
    }
}

* {
    margin: 0;
    padding: 0;
    list-style: none;
}

.screen_out {
    display: none;
}

header {
    background-color: black;
    padding: 0 40px;

    h1 {
        @include text-indent;
        width: 100px;
        height: 80px;
        background: url(./img/8th_logo_white.png) center / 60px 50px no-repeat;
    }

    nav {
        @include grid(1600px);
        @include center(space-between);
        margin: auto;
    }

    ul {
        display: flex;
        width: fit-content;
        justify-content: right;
    }

    a::after {
        content: "";
        position: absolute;
        bottom: -5px;
        left: 0;
        width: 0;
        height: 1.5px;
        background-color: aliceblue;
        border-radius: 50%;
        transition: .2s ease-in;
    }

    li {
        padding: 1rem 2rem;

        &:hover a::after {
            width: 100%;
        }
    }

    a {
        position: relative;
        color: aliceblue;
        text-decoration: none;
        text-transform: uppercase;
        font-size: 16px;
        font-family: $content;
        width: fit-content;
    }
}

main {
    background-image: url(img/stars.png);

    .slogan {
        height: 100vh;
        @include grid(1600px);

        .orbit {
            width: 600px;
            height: 600px;
            margin: auto;
            animation: updown 3s linear infinite;
        }

        ul:nth-child(3) li {
            color: $text-color-W;

            text: {
                transform: uppercase;
                align: center;
            }

            font: {
                size: 1.5rem;
                family: $content;
            }

            cursor: default;
            line-height: 120%;
        }

        &>p {
            position: relative;
            color: grey;
            margin: 2rem;
            animation: opacity 3s ease-in infinite;

            text: {
                align: center;
                transform: uppercase;
            }

            &::after {
                content: "";
                position: absolute;
                bottom: 0px;
                left: 50%;
                transform: translate(-50%, 110%) rotate(-45deg);
                width: 15px;
                height: 15px;

                border: {
                    bottom: 1px solid grey;
                    left: 1px solid grey;
                }
            }
        }
    }

    .about {

        h2 {
            @include h2;
            @include font($content, 28px, bold);
        }

        article:nth-of-type(1) {
            display: flex;
            @include center(center);

            img {
                @include width(600px)
            }

            div {
                position: relative;
                transform: translateY(50%);
                line-height: 150%;
                color: $text-color-W;

                h3 {
                    @include h3;
                    @include font($content, 20px, bold);
                }

                ul {
                    margin-top: 5%;
                    line-height: 130%;
                    word-break: keep-all;
                    color: $text-color-W;

                    p {
                        @include p(16px);
                    }
                }

                button {
                    padding: 5px 10px;
                    background: transparent;
                    border: 1px solid $text-color-W;
                    border-radius: 2px;
                    text-transform: uppercase;
                    color: $text-color-W;
                    position: absolute;
                    left: 0;
                    bottom: -30%;
                    opacity: .4;

                    &:hover {
                        opacity: 1;
                        cursor: pointer;
                    }
                }
            }

            &+details {
                @include grid(1200px);
                display: none;

                ul {
                    display: grid;
                    margin-top: 2rem;
                    grid-template-columns: repeat(8, 1fr);

                    li {
                        color: $text-color-W;
                        height: 160px;
                        background: url(img/html5.svg) center / 50% no-repeat;
                        position: relative;

                        span {
                            text-transform: uppercase;
                            position: absolute;
                            left: 50%;
                            bottom: 5%;
                            transform: translate(-50%, -50%);
                            font-size: 12px;
                            width: 100%;
                            text-align: center;
                        }

                        &.css {
                            background-image: url(img/css.svg);
                        }

                        &.js {
                            background-image: url(img/square-js.svg);
                        }

                        &.jquery {
                            background-image: url(img/jquery.svg);
                        }

                        &.github {
                            background-image: url(img/github.png);
                        }

                        &.scss {
                            background-image: url(img/sass.png);
                        }

                        &.figma {
                            background-image: url(img/figma.png);
                        }

                        &.photoshop {
                            background-image: url(img/photoshop.png);
                        }

                        &.illur {
                            background-image: url(img/illustrator.png);
                        }

                        &.premier {
                            background-image: url(img/premiere-pro.png);
                        }

                        &.affect {
                            background-image: url(img/after-effects.png);
                        }
                    }
                }

                &.on {
                    display: block;
                }
            }
        }

        .study {

            h3 {
                @include h2;
                @include font($content, 24px, bold);
            }

            &>ul {
                display: flex;
                gap: 10px;
                justify-content: center;

                .container {
                    position: relative;
                    border-radius: 12px;
                    @include width(300px);
                    height: 360px;
                    padding: 16px;
                    box-sizing: border-box;

                    &:hover .card {
                        transform: rotateY(180deg);
                    }

                    .card {
                        position: absolute;
                        width: 100%;
                        height: 100%;
                        transform-style: preserve-3d;
                        transition: all .5s ease;

                        .front {
                            position: absolute;
                            width: 100%;
                            height: 100%;
                            border: 2px solid $text-color-W;
                            box-sizing: border-box;
                            border-radius: 12px;
                            backface-visibility: hidden;
                            color: $text-color-W;
                            background: url(img/html5.svg) center / 50% no-repeat;
                            animation: updown 5s ease-out infinite;

                            &.css {
                                background-image: url(img/css.svg);
                                animation: updown 5.1s linear infinite;
                            }

                            &.vanila {
                                background-image: url(img/square-js.svg);
                                animation: updown 5.2s linear infinite;
                            }

                            &.jquery {
                                background-image: url(img/jquery.svg);
                                animation: updown 5.3s linear infinite;
                            }

                            span {
                                @include font($content, 20px, light);
                                position: absolute;
                                left: 50%;
                                bottom: 0;
                                transform: translate(-50%, -100%);
                            }
                        }

                        .back {
                            position: absolute;
                            width: 100%;
                            height: 100%;
                            backface-visibility: hidden;
                            transform: rotateY(180deg);
                            border-radius: 12px;
                            border: 2px solid $text-color-W;
                            padding: 0 16px;
                            box-sizing: border-box;

                            h4 {
                                @include h3;
                                @include font($title, 16px, bold);
                                margin: 20px 0;
                                position: relative;
                                word-break: keep-all;

                                &::after {
                                    content: "";
                                    position: absolute;
                                    left: 0;
                                    bottom: -8px;
                                    width: 100%;
                                    height: 1px;
                                    background-color: rgba($color: #fff, $alpha: .3);
                                }

                                &+p {
                                    @include font($content, 12px, light);
                                    color: $text-color-W;
                                    margin: 0 0 28px;
                                    text-align: center;
                                    line-height: 130%;
                                    word-break: keep-all
                                }
                            }

                            ul {
                                display: grid;
                                grid-template-columns: repeat(2, 1fr);
                                column-gap: 5px;

                                li {
                                    @include p(14px);
                                    transform: translateX(4%);
                                    margin: 0 0 16px;
                                    text-transform: uppercase;
                                    word-break: keep-all;
                                    line-height: 120%;
                                }
                            }


                            a {
                                border: 1px solid $text-color-W;
                                text-decoration: none;
                                color: $text-color-W;
                                padding: 5px 10px;
                                @include font($content, 14px, light);
                                position: absolute;
                                right: 20px;
                                bottom: 20px;
                                opacity: .3;

                                &:hover {
                                    opacity: 1;
                                }
                            }
                        }
                    }

                    span {
                        color: $text-color-W;
                        text-transform: uppercase;
                    }
                }
            }
        }

        .merit {
            position: relative;
            height: 100%;
            margin-top: 200px;

            svg {
                opacity: .8;
                position: fixed;
                top: 50%;
                left: 50%;
                transform: translateX(-50%);

                .path {
                    opacity: 0;
                    transition: ease-in-out .5s;

                    &.on {
                        opacity: 1;
                        transition: ease-in-out .5s;
                    }
                }
            }

            .wrap {
                top: 0;
                overflow: hidden;
                position: relative;
                height: 200vh;

                ul {
                    display: grid;
                    grid-template-columns: repeat(2, 1fr);
                    grid-template-rows: repeat(6, 1fr);
                    @include grid(1240px);
                    row-gap: 1.5rem;
                    column-gap: (1240px / 3);
                    height: 100%;
                    

                    .good {
                        border-radius: 12px;
                        @include width(450px);
                        @include height(150px);
                        padding: 1.5rem .2rem 0rem;
                        border: 2px solid $text-color-W;
                        background-color: #000000;
                        position: relative;
                        transform: translateY(100%);
                        opacity: 0;
                        transition: ease-in-out .7s ;

                        &:hover {
                            background-color: #fff;
                        }

                        h4 {
                            @include h3;
                            @include font($title, 1.5rem, bold);
                            margin: 0 0 16px;
                            color: $text-color-W;
                        }

                        p {
                            word-break: keep-all;
                            @include font($content, 1.1rem, light);
                            line-height: 130%;
                            text-align: center;
                            color: $text-color-W;
                        }

                        &:hover h4,  &:hover p {
                            color: #000000;
                        }

                        &.left {
                            grid-area: left;
                        }
                        &.left2 {
                            grid-area: left2;
                        }
                        &.left3 {
                            grid-area: left3;
                        }
                        &.right {
                            grid-area: right;
                            right: 0;
                            
                        }
                        &.right2 {
                            grid-area: right2;
                            
                        }
                        &.right3 {
                            grid-area: right3;
                            
                        }

                        &.on {
                            transform: translateY(0);
                            opacity: 1;
                        }
                    }

                    grid-template-areas: 
                    "left." ".right"
                    "left2." ".right2"
                    "left3." ".right3";

                }


            }


        }



    }

    .back-drop {
        position: fixed;
        top: 0;
        left: 0;
        width: 100vw;
        height: 100vh;
        background-color: #000000;
        opacity: .3;
        z-index: 0;
        display: none;

        &.on {
            display: block;
        }
    }

}
